packagegui.custom_php_global_functions:

	function sync_package_smokeping() {
	    conf_mount_rw();
	    config_lock();
	    global $config;
	    global $input_errors;
	    $smokeping_config =&amp; $_POST;

            exec("/bin/echo " . $smokeping_config['slavesecret'] . " &gt; /usr/local/etc/smokeping/slavesecrets.conf");
            exec("sed -i -e \"s:NO:YES:g\" /usr/local/etc/rc.d/smokeping");
            if($smokeping_config['slavesecret'] {
              exec("sed -i -e \"/slavename=/ s:=.*:=$smokeping_config['slavesecret']:\" /usr/local/etc/rc.d/smokeping");
            }

            /*
	    write_rcfile(array(
		    "file" => "smokeping.sh",
		    "start" => "service smokeping onestart",
		    "stop" => "service smokeping onestop"
		    "restart" => "service smokeping restart"
		    )
	    );
            */
	    restart_service("smokeping");
	    conf_mount_ro();
	    config_unlock();
	}

packagegui.custom_php_install_command:

	global $config, $g;

	conf_mount_rw();

	/* create a few directories and ensure the sample files are in place */
        exec("/usr/bin/touch /usr/local/etc/smokeping/slavesecrets.conf");
        exec("/bin/chmod /usr/local/etc/smokeping/slavesecrets.conf");
        exec("/usr/sbin/chown smokeping:smokeping /usr/local/etc/smokeping/slavesecrets.conf");
        exec("ln -s /usr/local/sbin/fping /usr/bin/fping");

	conf_mount_ro();

packagegui.custom_php_validation_command:

          conf_mount_rw();
          global $config;
          global $input_errors;
          $smokeping_config =&amp; $_POST;

          exec("/usr/bin/fetch $smokeping_config['master']");

          $fsmokeping = './smokeping.cgi';

          if (file_exists($fsmokeping)) {
            exec("/bin/rm $fsmokeping");
          } else {
            $input_errors[]="Master URL is down or contains an invalid address.";
          }

         if($smokeping_config['slavesecret'] and $smokeping_config['slavesecretagain']) {
          if($smokeping_config['slavesecret'] &#33;= $smokeping_config['slavesecretagain']) {
            $input_errors[] = "The provided passwords did not match.";
           }
         } else {
            $input_errors[] = "You must provide (and confirm) smokeping's password.";
         }
	conf_mount_ro();
